import { Component, Output } from '@angular/core';

@Component({
  selector: 'sidebar-header',
  templateUrl: './sidebar-header.component.html',
})
export class SidebarHeaderComponent {
  @Output() mobileOrientation: 'start' | 'end';
  styles: {
    mobileOrientation: {
      start: string;
      end: string;
    };
  };

  constructor() {
    this.mobileOrientation = 'end';
    this.styles = {
      mobileOrientation: {
        start: 'left-0',
        end: 'right-0',
      },
    };
  }
}
<div
  class="sticky top-0 z-10 mb-6 flex items-center justify-center bg-gray-900 pb-6 pt-3"
>
  <img src="./assets/images/2.png" width="80" height="90" alt="Enoch Ndika" />
</div>
